package de.plushnikov.intellij.lombok.extension;

import com.intellij.openapi.actionSystem.DataContext;
import com.intellij.psi.PsiElement;
import com.intellij.refactoring.move.MoveHandlerDelegate;
import org.jetbrains.annotations.Nullable;

/**
 * "Move"-Handler for methods generated by lombok (TODO)
 */
public class LombokElementMoveHandler extends MoveHandlerDelegate {
  @Override
  public boolean isValidTarget(PsiElement psiElement, PsiElement[] sources) {
    return super.isValidTarget(psiElement, sources);    //To change body of overridden methods use File | Settings | File Templates.
  }

  @Override
  public boolean canMove(DataContext dataContext) {
    return super.canMove(dataContext);    //To change body of overridden methods use File | Settings | File Templates.
  }

  @Override
  public boolean canMove(PsiElement[] elements, @Nullable PsiElement targetContainer) {
    return super.canMove(elements, targetContainer);    //To change body of overridden methods use File | Settings | File Templates.
  }
}
